@prefix obf: <tag:stardog:api:obf:> .

[] a obf:Obfuscation ;
    obf:digest "SHA-256" ;
    obf:include [
        obf:position obf:any ;
        obf:pattern ".*"
    ] ;

    obf:exclude [
        obf:position obf:any ;
        obf:namespace "rdf"
    ] ;
    obf:exclude [
        obf:position obf:any ;
        obf:namespace "owl"
    ] ;
    obf:exclude [
        obf:position obf:predicate ;
        obf:namespace "rdfs"
    ] ;

